import styled from 'styled-components';

export const Wrapper = styled.div`
width: 80%;
height: 200px;
padding: 20px;
text-align: center;
display: grid;
grid-template: auto auto / 1fr;
`;
export const Heading = styled.h1`
margin: 0;
padding: 0;
/* font-size: 2rem */
`;

export const  Content = styled.p`
/* font-size: 1.5rem */
margin: 0;
`;
