import styled from 'styled-components';

export const Wrapper = styled.div`
background: var(--diceBackground);
width: 60px;
height: 60px;
display: flex;
align-items: center;
justify-content: center;
border-radius: 10px;
box-shadow: 0px 5px 5px rgba(0, 0, 0, 0.15);
`;

export const Number = styled.h2`
font-size: 2rem;
`;
